INSERT INTO wine (id, wine_name, type, country, grape, vintage, sweet, acidity, body, tannic, image, comment, price) VALUES (1, "Sauvignon blanc", "white", "France", "Sauvignon Blanc", "1996", "0", "4", "3", "1", "https://images.vivino.com/thumbs/Kw8JvF37Qxysy-Gz_NCoNA_pb_600x600.png", "잘 익은 과실의 향이 넘치고 특히 체리와 붉은 열매류 과일의 특징이 뛰어나다. 입 안에서는 Powerful하며 우아한 Body 감과 은은한 힘이 오래 남는다. 아마로네와 유사하나 가격은 훨씬 저렴하며 품질이 뛰어난 아마로네의 동생격 와인으로 매우 Value가 뛰어난 와인이다.", "10000")
INSERT INTO wine (id, wine_name, type, country, grape, vintage, sweet, acidity, body, tannic, image, comment, price) VALUES (2, "Lapola", "red", "South Korea", "Sauternes", "1958", "2", "1", "0", "4", "https://pds.joongang.co.kr/news/component/htmlphoto_mmdata/201808/24/a2c657f3-9abd-425e-999a-2bef2e88f160.jpg", "선명한 보라색을 띠며, 자두, 레드 커런트, 카시스, 허브 향이 피어 오른다. 입 안에서는 매끈하고 부드러운 타닌과 풍부한 과일 풍미가 입 안을 가득 메우는 레드와인이다.", "40000")
INSERT INTO wine (id, wine_name, type, country, grape, vintage, sweet, acidity, body, tannic, image, comment, price) VALUES (3, "Petrus Gold", "sparkling", "Germany", "Gewürztraminer", "2001", "3", "2", "1", "0", "https://leesliquorlv.com/wp-content/uploads/2020/03/Algodon-Blue-Label-Cabernet-Sauvignon.jpg", "연둣빛이 감도는 골든 옐로우 색을 띠며 파파야, 키위 등의 신선한 열대 과일 향과 생동감 있는 청사과, 배 향 등이 어우러진다. 입 안에서는 적당하고 부드러운 산미가 식욕을 돋우고, 시원한 미네랄 워터의 감촉과 함께 오크 숙성에서 비롯한 견과류의 풍미가 인상적인 피니쉬를 주는 와인이다.", "150000")
INSERT INTO wine (id, wine_name, type, country, grape, vintage, sweet, acidity, body, tannic, image, comment, price) VALUES (4, "Sauvignon blue", "rose", "Guatemala", "Pinot NoirL", "2007", "3", "2", "4", "1", "https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1589557265-tc-whisperingangel-1589557251.jpg?crop=1xw:1xh;center,top&resize=480:*", "시간이 지날수록 붉은 빛의 토니 색을 띠고 붉은 과일 향과 약간의 검은 과일 향, 호두, 아몬드 등의 견과류 향이 느껴진다. 입 안에서는 오크 숙성으로 인한 섬세함한 타닌과 부드러움이 인상적이며 시간이 지날수록 더욱 부드럽고 복합적인 과실향이 어우러지는 와인이다.", "13000")
